import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import './App.css';

const API_BASE_URL = process.env.NODE_ENV === 'production' ? '' : 'http://localhost:8000';

function App() {
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [suggestedPrompts, setSuggestedPrompts] = useState([]);
  const [conversationEnded, setConversationEnded] = useState(false);
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  useEffect(() => {
    fetchSuggestedPrompts();
  }, []);

  const fetchSuggestedPrompts = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/api/suggested-prompts`);
      setSuggestedPrompts(response.data.prompts);
    } catch (error) {
      console.error('Error fetching suggested prompts:', error);
    }
  };

  const sendMessage = async (messageText) => {
    if (!messageText.trim() || isLoading || conversationEnded) return;

    const userMessage = { role: 'user', content: messageText };
    setMessages(prev => [...prev, userMessage]);
    setInputMessage('');
    setIsLoading(true);

    try {
      const response = await axios.post(`${API_BASE_URL}/api/chat`, {
        message: messageText
      });

      const agentMessage = {
        role: 'assistant',
        content: response.data.response,
        tools_used: response.data.tools_used,
        pdf_data: response.data.pdf_data,
        pdf_filename: response.data.pdf_filename
      };

      setMessages(prev => [...prev, agentMessage]);

      if (response.data.conversation_ended) {
        setConversationEnded(true);
      }

    } catch (error) {
      console.error('Error sending message:', error);
      const errorMessage = {
        role: 'assistant',
        content: 'Sorry, I encountered an error. Please try again.',
        isError: true
      };
      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setIsLoading(false);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    sendMessage(inputMessage);
  };

  const handleSuggestedPrompt = (prompt) => {
    sendMessage(prompt);
  };

  const downloadPDF = (pdfData, filename) => {
    const link = document.createElement('a');
    link.href = `data:application/pdf;base64,${pdfData}`;
    link.download = filename;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  const resetConversation = () => {
    setMessages([]);
    setConversationEnded(false);
  };

  return (
    <div className="App">
      <header className="app-header">
        <h1>🤖 LangGraph Agent Chat</h1>
        <p>Ask me anything about AI, machine learning, or request a PDF export!</p>
      </header>

      <div className="chat-container">
        <div className="messages-container">
          {messages.length === 0 ? (
            <div className="welcome-message">
              <h3>Welcome! Try one of these suggested prompts:</h3>
              <div className="suggested-prompts">
                {suggestedPrompts.map((prompt, index) => (
                  <button
                    key={index}
                    className="suggested-prompt-btn"
                    onClick={() => handleSuggestedPrompt(prompt)}
                    disabled={isLoading || conversationEnded}
                  >
                    {prompt}
                  </button>
                ))}
              </div>
            </div>
          ) : (
            messages.map((message, index) => (
              <div key={index} className={`message ${message.role}`}>
                <div className="message-content">
                  <div className="message-text">
                    {message.content.split('\n').map((line, i) => (
                      <p key={i}>{line}</p>
                    ))}
                  </div>

                  {message.tools_used && message.tools_used.length > 0 && (
                    <div className="tools-used">
                      <strong>🔧 Tools Used:</strong> {message.tools_used.join(', ')}
                    </div>
                  )}

                  {message.pdf_data && (
                    <div className="pdf-download">
                      <button
                        className="pdf-download-btn"
                        onClick={() => downloadPDF(message.pdf_data, message.pdf_filename)}
                      >
                        📄 Download PDF: {message.pdf_filename}
                      </button>
                    </div>
                  )}

                  {message.isError && (
                    <div className="error-message">
                      ⚠️ {message.content}
                    </div>
                  )}
                </div>
              </div>
            ))
          )}

          {isLoading && (
            <div className="message assistant">
              <div className="message-content">
                <div className="loading-indicator">
                  <div className="typing-dots">
                    <span></span>
                    <span></span>
                    <span></span>
                  </div>
                  <span>Agent is thinking...</span>
                </div>
              </div>
            </div>
          )}

          <div ref={messagesEndRef} />
        </div>

        {conversationEnded && (
          <div className="conversation-ended">
            <p>💬 Conversation ended. Start a new conversation to continue.</p>
            <button className="reset-btn" onClick={resetConversation}>
              Start New Conversation
            </button>
          </div>
        )}

        <form onSubmit={handleSubmit} className="input-form">
          <div className="input-container">
            <input
              type="text"
              value={inputMessage}
              onChange={(e) => setInputMessage(e.target.value)}
              placeholder={conversationEnded ? "Conversation ended. Start a new one." : "Type your message here..."}
              disabled={isLoading || conversationEnded}
              className="message-input"
            />
            <button
              type="submit"
              disabled={!inputMessage.trim() || isLoading || conversationEnded}
              className="send-button"
            >
              {isLoading ? '⏳' : '📤'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default App;
